function searchSuperhero() {
    const searchInput = document.getElementById('searchInput').value.trim();
    const resultContainer = document.getElementById('result');

    if (searchInput === '') {
        resultContainer.innerHTML = '<p>Please enter a superhero name.</p>';
        return;
    }

    const superheroData = getSuperheroData(searchInput);

    if (superheroData) {
        const { name, power, image } = superheroData;
        resultContainer.innerHTML = `
            <h2>${name}</h2>
            <p>Superpower: ${power}</p>
            <img src="${image}" alt="${name}" width="200">
        `;
    } else {
        resultContainer.innerHTML = '<p>Superhero not found.</p>';
    }
}

function getSuperheroData(name) {
    const superheroes = {
        'superman': { name: 'Superman', power: 'Super strength, flight', image: 'data:image/jpeg;base64,' },
        'spiderman': { name: 'Spiderman', power: 'Wall-crawling, spider sense', image: 'data:image/jpeg;base64,' },
        'batman': { name: 'Batman', power: 'Master detective, martial artist', image: 'data:image/jpeg;base64,' },
    };

    return superheroes[name.toLowerCase()];
}













